INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'395', N'高教版师范院校语文教育学专业教材?师范院校汉语言文学教育专业语文课程教学论系列教材：语文课程教学资源 ', N'《医护英语水平考试（护理类）词汇手册（第1、2级）（2008版）》以考试大纲为依据，收录了一、二级考试必须掌握的全部词汇，标“▲”号的为METS一级词汇，所有单词均按字母顺序排列。为帮助广大在职医护工作者和在校的医护学生系统地学习并掌握医学专业英语，为促进我国医护英语教育与国际医护教育接轨，为我国广大医护工作者参加国际交流做准备，我们在编写《医护英语水平考试（护理类）词汇手册（第1、2级）（2008版）》时，不但每个单词都标有音标，还提供了典型的例句和词汇扩展，使读者既知其然，又知其所以然。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 ')
