INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'396', N'大学德语1：教学参考书（第3版）                                   ', N'《职业教育集团化办学的理论研究与实践探索》是教育部教育改革和发展战略与政策研究重大课题“职业教育集团化办学的理论研究与实践探索”的成果专集。全书由课题研究总报告、课题专题研究报告、地方案例研究报告等部分组成，对我国职业教育集团化办学的现状，职业教育集团化办学的国际比较，职业教育集团化办学的有关理论研究等进行了综合研究，最后提出了职业教育集团化办学的有关政策建议。《职业教育集团化办学的理论研究与实践探索》可供从事职业教育与人事劳动管理人员和科研人员，从事职业教育与培训工作的人员参考使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
