INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'403', N'研究生数学：应用偏微分方程讲义                                    ', N'剑桥通用英语五级证书考试是在全球受到广泛认可的高质量的国际英语考试．考试合格者可获得终生有效的英语能力证书。该考试分为5个级别。分别为KBEngsTs（KET）。PnaEngs　Ts（PET），F5EfcanEngs，（FEE）。EfcanAvancEngs（EAE）和EfcafPfcncynEngs（EPE）。其中，KET、ET和FCE考试已经由教育部考试中心引入中国。北京英语水平考试（BngEngsTsngSLJSPn。简P尔BETS）由北京市民讲外语活动组委会、北京市人民政府外事办公室和英国剑桥大学考试委员会外语考试部共同推出。罱用剑轿通用英语五级系列证书考试中的KET、PET、FEE三个级别．涉及阅读、写作、听力和口语等方面．全面考查考生英语语言的综合运用能力，通过BETS考试即可获得北京英语水平考试证书及剑桥通用英语系列考试证书。北京英语水平考试系列用书改编自剑矫通用英语系列证书考试KET、PET、FEE的英文原版教材，由高等教育出版社独家引进出版。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
