INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'411', N'数学类专业学习辅导丛书：常微分方程学习辅导与习题解答                         ', N'本书是“数学教育系列教材”（普通高等教育“十一五”国家级规划教材）之一，是关于数学教育基本理论与实践的概述，帮助具有数学专业知识的学生获得有关数学教育的基本知识和技能。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
