INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'437', N'财经商贸类职业培训用书：出纳员岗位实训                                ', N'《数码服装设计与表现技法：CDRAW》是服装设计专业系列教材之一。《数码服装设计与表现技法：CDRAW》按照服装设计基础的知识结构要求，以当今最普及，最受欢迎的平面绘图软件之一――“CDRAW”，采用实例讲解的方法，进行服装款式、服装图案、服装面料、服装辅料．服装画、服装样板、服装工业制单等的设计与制作。《数码服装设计与表现技法：CDRAW》涉及的服装专业知识面广，操作技能入门浅，尤其是贯穿《数码服装设计与表现技法：CDRAW》的绘图软件本身就是一个非常优秀、非常普及的软件，也是服装专业必须掌握的常用软件。《数码服装设计与表现技法：CDRAW》的专业知识丰富全面，绘制技法科学实用，视觉效果精彩，适用于各级各类服装艺术设计院校的学生使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
