INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'474', N'定性与半定量物理学                                          ', N'本教材主要阐述确定性信号的时域分析和变换域分析，线性非时变系统的描述与特性以及信号通过线性非时变系统的时域分析与变换域分析；并简要介绍信号与系统的基本理论在生物神经网络和通信系统中的应用。本书采用连续和离散并行、先时域后变换域的结构体系，突出以信号分析为基础的理念，从信号表示的角度引入信号的频谱。强调基本理论、基本概念和基本方法，淡化计算技巧，引入MATLAB作为信号与系统分析的工具。同时，本书还配有大量例题和习题，注重难点和重点的解释与分析。本教材于2006年被列入“普通高等教育‘十一五’国家级规划教材”。本教材可作为电子信息工程、通信工程、信息工程、自动化、生物医学工程、计算机等专业的本科生教材，也可供有关科技工作者自学参考使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
