INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'499', N'多层统计分析模型：方法与应用                                     ', N'本书是在初等概率论、测度论和泛函分析初步的基础上展开的。全书共分三大部分：一、高等概率的基本概念与工具，诸如随机元(含特例随机变量                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ')
