INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'510', N'高等学校教材：数学建模与数学实验（附光盘）                              ', N'《商务礼仪与公关》是中等职业学校财经商贸类专业教学用书。全书以“够用、实效”的原则构建教材体系和内容，突出应用能力的培养，注重科学性、系统性和实用性的统一。《商务礼仪与公关》共分两个部分、八个单元，分别是：第一部分商务礼仪，包括商务形象礼仪、商务见面礼仪、商务场合礼仪和商务客户礼仪四个单元；第二部分商务公关，包括企业形象设计、商务新闻传播、商务专题活动和商务危机处理四个单元。全书采用项目任务式教学，将总体任务细化成多个子任务，使中职学生通过学习，掌握成为高素质的劳动者所必需的商务礼仪与公关的基本理论和技能，以提升自身修养和综合素质，增强就业能力。《商务礼仪与公关》可作为中等职业学校相关专业的必修课程教材，也可作为实际岗位工作人员的培训用书和学习参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               ')
