INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'512', N'商务礼仪与公关                                            ', N'《电工电子技术》是普通高等教育“十一五”国家级规划教材（高职高专教育），是《电工电子技术（少学时）》（第2版）的修订版。内容符合教育部最新制订的高职高专教育电工电子技术课程教学基本要求。内容包括：直流电路、正弦交流电路、三相电路、动态电路的分析、磁路和变压器、异步电动机、继电―接触器控制、工厂供电与安全用电、电工测量、电子电路中常用的器件、基本放大电路、集成运算放大器、直流稳压电源、逻辑门电路、组合逻辑电路、触发器及其应用、555电路及应用、D／A和A／D转换器，共计18章。每章后附有小结和习题。书末附有部分习题答案，便于自学。《电工电子技术》层次分明，条理清晰，结构合理，重点突出，概念阐述清楚、准确，内容深入浅出、通俗易懂。《电工电子技术》可作为高等职业学校、高等专科学校、成人高校及本科院校举办的二级职业技术学院和民办高校非电类专业的教材，也可供工程技术人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
