INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'515', N'全国高职高专教育“十一五”规划教材：生物分离技术                           ', N'《电机及拖动（第3版）》是普通高等教育“十一五”国家级规划教材（高职高专教育），是在2004年6月第2版的基础上修订而成的。全书共分8章，主要内容有直流电机、直流电动机的电力拖动、变压器、交流异步电动机、三相异步电动机的电力拖动、同步电机及同步电动机的电力拖动、微控电机和电力拖动系统中电动机的选择。《电机及拖动（第3版）》可作为高等职业学校、高等专科学校、成人高校及本科院校举办的二级职业技术学院和民办高校的电气自动化技术、生产过程自动化技术、供用电技术、机电一体化技术和电气化铁道技术等专业的“电机与拖动”课程的教材，也可供有关工程技术人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ')
