INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'552', N'全国中小学教师远程非学历培训课程资源开发项目：物理新课程教学案例研究（附光盘1张）          ', N'《初中化学新课程教学设计及教学案例研究》是全国中小学教师远程非学历培训课程资源开发项目研究成果。致力于帮助中学化学教师解决新课程实施中遇到的问题，提高教师的教育教学研究能力。全书共四大专题，分别从教学设计和教学案例研究两个角度对初中化学新课程的教学内容进行具体的解剖分析。主要包括：初中化学新课程教学设计、具体教学设计的案例及评析、教学案例研究方法、具体教学案例评析等内容。《初中化学新课程教学设计及教学案例研究》适合初中化学教师、科学教师及相关学科教研人员阅读、参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ')
