INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'555', N'回归与拯救：儿童提问与早期教育                                    ', N'本书以教学情境和职业情境为引导，以案例为驱动，以情景模拟、案例欣赏、相关链接、问题诊断、实训平台为主要板块，较为完整地介绍了公交类、事务类、求职类、礼仪类、策划类、传播类、商务类、调研类、财经类、法律类和科技类等多种应用文体的相关知识和写作要求，具有较强的科学性、可读性和操作性。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
