INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'557', N'普通高等教育“十一五”国家级规划教材：应用写作                            ', N'本书主要阐述会计的基本理论、基本方法及基本操作技能。在论述会计学的有关概念和基本理论问题的基础上，重点阐明会计核算的各种专门方法，同时也一般性地介绍了会计报表分析、会计监督和会计电算化的相关知识。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
