INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'562', N'高等学校法学专业研究生教学用书：法理学高阶                              ', N'《法理学视野下的冲突法》一书力图打破现有冲突法体系观念的束缚，从更广阔的法理学视角重新审视、检讨和构建冲突法的基本概念、制度及其与相关法律问题的关系，并在此基础上探讨一些重要法律适用原则和规则的适用模式和方法。除了采取比较法研究方法以外，本书注意采用实证分析和国家政策考量，特别关注中国法律制度的特点、中国司法实践的要求、中国国际地位的提升和中国在国际民商事交往中角色的变化，以提出适合中国实际的冲突法理念、概念、制度和规则。因此，本书对于法律冲突的分类、法律适用规则与冲突规范的关系、连结因素的形成和选择等问题的观点和主张有利于建构中国特色的国际私法理论体系和立法体系。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           ')
GO

INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'563', N'中外关系鉴览1950-2005：中国与大国关系定量衡量                        ', N'本书从大学战略、大学战略规划、大学战略管理等基本概念的界定着手，回顾了大学战略规划的历史过程，介绍了大学战略规划与管理的基本方法，阐述了大学战略规划的基本理论，分析了大学战略规划的系统环境、管理体制与领导体制，论述了大学战略规划的编制、实施与评估，剖析了国外大学战略规划与管理的实践案例及其启示与借鉴。最后，展望了中国大学战略规划与管理的未来变化与发展的基本趋势。本书对高等教育行政管理部门、各高等院校等实施大学战略规划与管理具有较大的指导作用和参考价值，可作为高等教育管理相关领域的研究生教学参考用书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
