INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'575', N'高等学校教材：数学分析讲义（第5版）（上册）                             ', N'《普通高等教育“十一五”国家级规划教材?偏微分方程》对偏微分方程的古典理论作了严谨的介绍和论证，在内容、概念与方法等方面注重与现代偏微分方程知识之间的内在联系，对现代偏微分方程知识作了基本的阐述，注意各个数学分支知识在偏微分方程中的应用。《普通高等教育“十一五”国家级规划教材?偏微分方程》内容丰富，方法多样，技巧性强，并配有大量的例题和习题，难易兼顾，层次分明。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ')
