INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'582', N'心理剧与创伤：伤痛的行动演出                                     ', N'《中国传统文化》为全国高职高专教育“十一五”规划教材之人文素质教育系列中的一本，是根据高等职业教育“以服务为宗旨，以就业为导向，全面提高高职学生的人文素养”办学理念编写的。内容上包括中国传统文化的生成、发展与基本精神，以及古代哲学、文学、艺术、科技、教育、宗教与民俗等知识，力求以提高学生人文素质，传递人文精神与科学精神为基本价值取向，拓展学生视野，开拓学生思维，陶冶学生情感，丰富学生人文知识。编写上注重以高等职业教育的人才培养目标为切入点，以学生的人文知识拓展和人文素养培养为基本目标；内容上，强调科学性、知识性、趣味性相统一，强调贴近学生生活；体例上，由“情境导入”、“案例欣赏”、“点击核心”、“知识链接”、“互动平台”、“网上冲浪”等模块构成；练习上精心设计，力求体现教材的针对性和可操作性。《中国传统文化》适用于应用性、技能型人才培养的各类教育，也可作为社会相关人员的参考书及培训用书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ')
