INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'586', N'普通高等教育“十一五”国家级规划教材：新编实用英语学学练练考考1（附VCD光盘1张）         ', N'《德语难点解读》是关于讲解“德语语法和词汇难点”的专著，具体包括了：介词使用中易出现的错误、易混淆的介词、句法中一致性的错误、定语和相关词的一致性、关系代词和相关词间的一致性、疾病名词前使用不定冠词或零冠词、零冠词的用法等方面的内容。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ')
