INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'604', N'中国法学教科书?原理与应用系列：商法教程                               ', N'《电机与拖动（第2版）》分上、下两篇。上篇为电机与拖动基础，包括磁路、变压器、异步电机的基本理论、异步电机的电力拖动、同步电机的基本理论、同步电机的电力拖动、直流电机的基本理论、直流电机的电力拖动、控制电机、电动机的选择、电力拖动系统的动力学基础等。下篇为电机与拖动实验，包括单相变压器实验、三相变压器实验、笼型三相异步电动机实验、绕线型三相异步电动机实验、三相同步电动机实验、三相同步发电机实验、直流发电机实验、直流电动机实验等。《电机与拖动（第2版）》可作为高等学校自动化和电气工程及其自动化等专业的教科书，也可作为成人高等教育的教材，还可以供有关工程技术人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
