INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'612', N'成功人才素质论                                            ', N'《电路基础》是普通高等教育“十一五”国家级规划教材。内容符合教育部高等学校电子信息科学与电气信息类基础课程教学指导分委员会制订的“电路理论基础”教学基本要求和“电路分析基础”教学基本要求。《电路基础》分为三篇：电阻电路、动态电路、稳态电路。具体内容为：基本概念和基本规律、电路分析的基本方法、电路定理、非线性电阻电路分析、动态电路的时域分析、动态电路的复频域分析、动态电路的状态变量分析、正弦稳态电路分析、三相电路和非正弦周期稳态电路分析，此外还包括分布参数电路、MATLAB语言简介以及OCAD／CaCIS、Psc简介三个附录。《电路基础》配有大量例题、习题和工程应用实例。《电路基础》可作为高等学校电气信息类各专业电路课程教材，也可作为科技人员的参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
