INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'616', N'全国英语等级考试PETS系列用书：全国英语等级考试分级词汇考点手册（第1级）             ', N'《高等学校教材：点集拓扑讲义（第3版）》讲述点集拓扑的基本知识，其基本内容涵盖：拓扑空间和连续映射的定义及其基本性质；构造新的拓扑空间的方法；各种拓扑不变性质。如连通性、分离性、紧致性、度量空间的完备性等，以及这些拓扑不变性质之间的相互关联；这些拓扑不变性质的可积、可遗传等性质；映射空间及其各种基本的拓扑。本次重版专门加入了一章讲述基本群及其应用，同时也补充和完善了关于朴素集合论方面的内容，增加介绍了与选择公理等价的一些常用命题。《点集拓扑讲义》可作为数学类专业点集拓扑课程的教材或教学参考书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ')
