INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'642', N'梨花雪后（套装上下册）                                        ', N'《英语泛读教程》全套共4册。每册15个单元，按阅读材料的内容和难度编排。本册每单元分为三部分。第一部分为阅读课文及练习，练习包括判断课文中心思想、阅读理解、课堂讨论和词汇练习。第二部分为快速阅读练习，提供三篇短文，要求在规定的时间内完成。第三部分为课外阅读，提供和课文长度相当的语言材料，配有阅读理解和思考题。书后附有练习答案，供教师和学生参考。《英语泛读教程4（第2版）》是其中的第四册，可供高等学校英语专业一、二年级使用，亦适用于同等程度的英语自学者。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ')
