INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'647', N'普通高等教育十五国家级规划教材：英语教师参考书（非英语专业专科用）                  ', N'《信号与线性系统（上）（第4版）》作者在第3版的基础上，根据当前信息和通信技术的发展，结合高校教学改革的形势和要求，综合近十年来的教学实践，对教材作出修订。与上版相比，《信号与线性系统（上）（第4版）》保留了大部分原有内容，最大的改动是增加了离散傅里叶变换和数字滤波器这两章。上册的具体内容是：绪论、连续时间系统的时域分析、连续信号的正交分解、连续时间系统的频域分析、连续时间系统的复频域分析、连续时间系统的系统函数。《信号与线性系统（上）（第4版）》可供普通高等学样电气信息类专业作为“信号与系统”课程的教材使用，也可供有关科技人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           ')
