INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'651', N'信号与线性系统（下）                                         ', N'《现代检测技术》为普通高等教育“十五”国家级规划教材。主要介绍现代检测技术的基础知识和各种物理、化学成分参量检测的原理、方法和技术。在教材内容组织上，为便于教学与自学，采用贴近工程应用实际的按被测参量进行分类编排的方法（即按计量学分类法）。着重讲述工程重要参量的常用检测方法与技术实现机理、工作原理和应用特点，通过典型实例介绍技术先进、适合工程实际需要的高性价比自动检测系统的设计方法。基于我国经济与社会可持续发展对环境保护与污染治理的强大需求和我国环保行业目前严重缺乏环保自动化方面工程技术人才的被动局面，本教材增加同类教材中通常没有的“水环境和水污染检测技术”及“环境空气与大气污染检测技术”两章。本教材内容包含的检测参量广，能较好地满足多类专业的宽口径教学需要，可供从事相关专业的科技和自动化工程技术人员参考。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ')
