INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'674', N'普通高等教育十五国家级规划教材：英语听力教程3（教师用书）（附赠MP3光盘1张）           ', N'《信号与系统》主要论述了信号与线性系统分析的基本理论和基本分析方法，理和方法。并详细介绍了信号与系统的基本概念、连续时间信号与系统的时域分析、连续时间信号与系统的频域分析、连续时间信号与系统的复频域分析、离散时间信号与系统韵时域分析、离散时间信号与系统的z域分析以及系统的状态变量分析法等内容，《信号与系统》既可作为高等工科院校通信与电子信息类专业的教材，也可供自学考试及成人教育有关专业选用，还可供研究生及有关科研人员参考使用。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ')
