INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'685', N'埃及艳后                                               ', N'《结构力学（上）》以结构力学的基本概念、基本原理及其科学运用为主线，以工程实践为背景，以学生素质与能力的提高为目标。其目的是为适应现代工程概念设计和结构分析计算机化后工程技术人员力学基本素质方面的要求，体现了“概念结构力学”的基本思想。在介绍各种方法的同时更强调“就是论道”和“由技入道”，力求使学生掌握结构力学的真谛。内容编写上还加强了启发性，引导学生积极思考和正确地分析工程实际问题。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
