INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'699', N'世界著名品牌100讲                                         ', N'《电子政务在企业中的应用》是教育部推荐教材，是根据电子政务应用专业教学大纲编写的。《电子政务在企业中的应用》主要内容有：政府与企业间电子政务应用概述，政务信息资源管理和利用，工商行政管理网上平台应用，网上办税工作平台应用，中国电子口岸平台应用，网上招投标系统平台应用，其他电子政务业务应用，政府一站式服务平台应用。《电子政务在企业中的应用》可作为职业院校电子政务应用专业的教学用书，也可作为各地公务员培训用书。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ')
