INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'706', N'普通高等教育“十五”国家级规划教材：人机工程学（附光盘）                       ', N'从未涉及的职场领域，“采购”与“销售”的博弈之道。在亚洲第一大零售企业这个舞台上，敌友难辨，谍影重重，职场之路永远步步惊心，无法预料下一个出局的是谁。两大国际知名卖场竞相力销，火爆上架，牢牢占领HOT人气畅销书榜首！                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
