INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'728', N'混合式学习的理论与实践                                        ', N'《工科数学分析基础（下册）（第2版）》第一版为教育部“高等教育面向21世纪教学内容和课程体系改革计划”的研究成果，是面向21世纪课程教材和教育部工科数学学科“九五”规划教材，同时又是普通高等教育“九五”国家级重点教材。第二版是普通高等教育“十五”国家级规划教材，保持了第一版的框架结构和主要特色。全书分为上下两册。上册主要内容为一元微积分和无穷级数，下册主要内容为多元函数微积分，常微分方程组，无限维分析入门。《工科数学分析基础（下册）（第2版）》在编写时，适当降低了某些内容的难度，并改写了部分内容，使得整体思路更加明确，更易被读者接受。从应用的需要考虑，增添了相关的内容。在习题的选配上，分为A、B两类，并增加了基本训练习题。《工科数学分析基础（下册）（第2版）》可供高等理工科院校对数学要求较高的非数学类专业本科生教材，也可供其他专业选用和社会读者阅读。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ')
