INSERT INTO [dbo].[big_data] ([id], [title], [detail]) VALUES (N'754', N'普通高等学校体育教育专业主干课系列教材：体育心理学                          ', N'本书包括上下两篇，阐述了构图学及其基本原理，并对构图进行案例分析。作者用学科前沿的精神、思想和观念重新演绎传统的构图学：从艺术家个案分析中揭示构图学的一般规律和原理；从心理学角度对艺术家风格进行探析，并揭示艺术家人格精神与构图的形式结构中存在的客观内在联系；把原本底于造型艺术技法范畴的构图，上升到美学意义上的理论分析与研究；客观、真实、系统地揭示人类视觉经验的积淀、演进对人类艺术发展史产生的深刻影响和重大意义，从而使本教材的系统性、科学性、实用性、理论性凸显出来。本书可作为高等教育本科美术专业学生的必修课教材。                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ')
